frappe.ui.form.on('Item', {
	refresh(frm) {
		// your code here
	},
	kategori_barang(frm){
	    if(frm.doc.kategori_barang == "Asset"){
	        frm.set_value("is_fixed_asset",1);
	        frm.set_value("auto_create_assets",1);
	        frm.set_value("asset_naming_series","DRW-ASS-.{abbr}.-.#####");
	        frm.set_value("is_stock_item",0)
	    }
	    if(frm.doc.kategori_barang == "Non Stock"){
	        frm.set_value("is_fixed_asset",0);
	        frm.set_value("auto_create_assets",0);
	        frm.set_value("asset_naming_series","");
	        frm.set_value("is_stock_item",0)
	    }
	    if(frm.doc.kategori_barang == "Non Asset / Stock"){
	        frm.set_value("is_fixed_asset",0)
	    }
	}
})

